/**
 * Generates a blur data URL for a Cloudinary image
 * @param src - The Cloudinary image URL
 * @returns A low-quality blur data URL
 */
export function getCloudinaryBlurDataURL(src: string): string {
  // Check if it's a Cloudinary URL
  if (!src.includes("cloudinary.com")) {
    // Return a simple gray blur for non-Cloudinary images
    return "data:image/jpeg;base64,"
  }

  // Extract the public ID from the Cloudinary URL
  const urlParts = src.split("/")
  const uploadIndex = urlParts.findIndex((part) => part === "upload")

  if (uploadIndex === -1) {
    return "data:image/jpeg;base64,"
  }

  // Reconstruct the base URL
  const baseUrl = urlParts.slice(0, uploadIndex + 1).join("/")
  const publicIdWithExtension = urlParts.slice(uploadIndex + 2).join("/")

  // Create a low-quality, small version for blur placeholder
  const blurUrl = `${baseUrl}/c_scale,w_10,q_auto:low,f_auto/${publicIdWithExtension}`

  return blurUrl
}

/**
 * Generates a simple base64 blur placeholder
 * @returns A base64 encoded blur image
 */
export function getSimpleBlurDataURL(): string {
  return "data:image/jpeg;base64,"
}
